  return(
        <ThemeProvider theme={theme}>
          <Router >
            <div>
              <Switch>
                <Route path="/home">
                  <Home />
                </Route>
                <Route path="/initiate">
                  <StartMessages />
                </Route>
                <Route path="/chat/:id" render={(props) => (
                  <Chat key={props.match.params.id} {...props} />)
                } />

                <Route path="/loader/:id" component={Loader}/> 
                <Route path="/">
                  <Home />
                </Route>
              </Switch>
            </div>
          </Router>
        </ThemeProvider>
  )
}

export default App;
