import React, {useEffect, useState}from 'react';
import './App.css';
import ChatBot from 'react-simple-chatbot';
import { ThemeProvider } from 'styled-components';
import ChatBanner from './ChatBanner';
import InfoIcon from '@material-ui/icons/Info';
import { FreeBreakfastOutlined, Info } from '@material-ui/icons';
import { Typography } from '@material-ui/core';
import styles from './Chat.css'; 
import {RemoveScrollBar} from 'react-remove-scroll-bar';
import firebase from 'firebase'

const steps = [
  {
    id: '1',
    message: "Welcome to " + COMPANY_NAME + "!",
    trigger: '2',
  },
  {
    id: '2',
    message: 'Would you like to look at our support articles or speak with an agent?',
    trigger: '3'
  },
  {
    id: '3',
    options: [
      { value: 'Articles', label: 'Articles', trigger: '4' },
      { value: 'Agent', label: 'Agent', trigger: '4' },
    ],
  },
  {
    id: '4',
    message: 'Please enter your full name.',
    trigger: 'name'
  },
  {
    id: 'name',
    user: true,
    trigger: '6',
  },
  {
    id: '6',
    component: <Connecting />,
    waitAction: true,
  },
  {
    id: '7',
    message: 'You are now connected with an agent.',
    trigger: 'message'
  },
  {
    id: 'message',
    user: true,
    trigger: 'messageHandler'
  },
  {
    id: 'messageHandler',
    component: <MessageHandler/>,
    waitAction: true
  },
  {
    id: 'handleAgentMessage',
    component: (
    <div>{agentMessage}</div>
    ),
    asMessage: true,
    end: true
  }
]

const theme = {
    background: '#f5f8fb',
    fontFamily: 'Helvetica Neue',
    headerBgColor: '#000000',
    headerFontColor: '#fff',
    headerFontSize: '15px',
    botBubbleColor: '#EF6C00',
    botFontColor: '#fff',
    userBubbleColor: '#fff',
    userFontColor: '#4a4a4a',
  };

export default class GhostChat extends React.Component{
    render(){
        return(
            <ThemeProvider theme={theme}>
                <ChatBot hideHeader={true} steps={steps} customStyle={{backgroundColor:'transparent', 'boxShadow': 'none'}} contentStyle={{height: this.props.height, backgroundColor:'transparent', display:'flex', flexDirection:'column', justifyContent:'flex-end' }} bubbleStyle={{maxWidth:'100%'}} style={{backgroundColor:'transparent'}} />
            </ThemeProvider>
        )
    }
}
