import React from 'react';
import './App.css';
import ChatBot from 'react-simple-chatbot';
import { ThemeProvider } from 'styled-components';
import ChatBanner from './ChatBanner';

const steps = [
    {
      id: '0',
      component: (
        <div style={{backgroundColor: 'green'}}> This is an example component </div>
      ),
      trigger: '1'
    },
    {
      id: '1',
      message: 'Welcome to Bonsai!',
      trigger: '2',
    },
    {
      id: '2',
      message: 'What service what !',
      trigger: '3'
    },
    {
      id: '3',
      options: [
        { value: 1, label: 'Number 1', trigger: '1' },
        { value: 2, label: 'Number 2', trigger: '1' },
        { value: 3, label: 'Number 3', trigger: '1' },
      ],
    },
  ];

  const theme = {
    background: '#f5f8fb',
    fontFamily: 'Helvetica Neue',
    headerBgColor: '#000000',
    headerFontColor: '#fff',
    headerFontSize: '15px',
    botBubbleColor: '#EF6C00',
    botFontColor: '#fff',
    userBubbleColor: '#fff',
    userFontColor: '#4a4a4a',
  };

function Chat(){
    return(
        <>
            <ChatBanner style={{backgroundColor:"black"}} />
            <ThemeProvider theme={theme}>
                <ChatBot hideHeader={true} enableSmoothScrolling={true} bubbleOptionStyle={{backgroundColor:'transparent', color: 'orange', borderWidth: 100, borderColor: 'black' }} contentStyle={{height:400}} steps={steps} />
            </ThemeProvider>
        </>


    )
}

export default Chat;
