const initState = {
    sessionID: "",

  }

  const rootReducer = (state = initState, action) => {
    console.log(action);
    if(action.type === 'UPDATE_SESSION'){
     let newPosts = state.posts.filter(post => {
       return post.id !== action.id
     });
     if(action.type === 'INIT_SESSION'){
        return {
          ...state,
          sessionID: action.id
        }
    }
     return {
       ...state,
       posts: newPosts
     }
    }
    return state;
  }

  export default rootReducer
