import React from 'react';
class Banner extends React.Component {

    constructor(props) {
      super(props);

      this.scrollHandler = this.scrollHandler.bind(this);

      this.state = {
        fadeIn: '',
        opacity:1
      };
    }

    componentDidMount() {
      window.addEventListener('scroll', this.scrollHandler);
      this.scrollHandler();
    }


    scrollHandler() {
      console.log(window.scrollY)
      if (window.scrollY + window.innerHeight < this.banner.offsetTop) {

        console.log("hmmmmm")
        //window.removeEventListener('scroll', this.scrollHandler);
      } else {
        console.log("okay")
      }

      var opacityChange = 1 - (window.scrollY/200);
      this.setState({opacity:opacityChange})
    }

    render(){
      return(
        <div style={{color:"white", opacity: this.state.opacity}} ref={(div) => { this.banner = div }}>
            <span>Awesome article</span>
        </div>
      )
    }
}

export default Banner;
