import React from 'react';
import './App.css';
import ChatBot from 'react-simple-chatbot';
import logo from './invstr-logo.png';
import propic1 from './propic.jpg';
import { Typography, Container } from '@material-ui/core';
import {useSpring, animated} from 'react-spring';
import { Parallax, ParallaxLayer } from '@react-spring/parallax';
import ScrollAnimation from 'react-animate-on-scroll';
import ScrollableAnchor from 'react-scrollable-anchor';
import "animate.css/animate.min.css";
import Banner from './Banner'
const steps = [
  {
    id: '0',
    message: 'Welcome to react chatbot!',
    trigger: '1',
  },
  {
    id: '1',
    message: 'Bye!',
    end: true,
  },
];


function App() {
  return(
    <div>
      <Container style={{backgroundColor:"black", height:200, top:0, left:0, position:"absolute"}}>
          <div>hello</div>
        </Container>
      <Parallax pages={2} >
        <ParallaxLayer offset={0} speed={1} >
          <Container style={{backgroundColor:"", paddingTop: 10, paddingBottom:20}}>
            <img src={logo} style={{height:50}}/>
            <Typography variant={"h6"} style={{color:"white"}}><b>Hey There </b> ðŸ‘‹</Typography>
            <Typography variant={"body"} style={{color:"white", opacity:0.8}}>Every year, there are billions of empty car seats going between cities, leading to congestion and pollution.</Typography>
          </Container>
          <ParallaxLayer offset={0} speed={0.5}>
            <Container  style={{backgroundColor:"",  paddingTop: 10, paddingBottom:10}}>
              <div style={{boxShadow: "2px 2px 3px #9999", backgroundColor:"white"}}>
                <Container  style={{backgroundColor:"",  paddingTop: 10, paddingBottom:10}}>
                <Typography variant={"subtitle1"} style={{color:"black"}}><b>Start a conversation</b></Typography>
                <img src={propic1} style={{height:60, width:60, borderRadius:30, position:'relative'}}/>
                <img src={logo} style={{height:60, borderWidth:5, borderColor:'black', width:60, borderRadius:30, left:10, position:'relative'}}/>
                </Container>
              </div>
            </Container>
          </ParallaxLayer>
        </ParallaxLayer>
      </Parallax>
    </div>
  )
}

export default App;
