import React from 'react';
import './App.css';
import ChatBot from 'react-simple-chatbot';
import logo from './invstr-logo.png';
import propic1 from './propic.jpg';
import { Typography, Container, Avatar, Button, Grid, GridItem } from '@material-ui/core';
import { AvatarGroup } from '@material-ui/lab';
import {useSpring, animated} from 'react-spring';
import { Parallax, ParallaxLayer } from '@react-spring/parallax';
import ScrollAnimation from 'react-animate-on-scroll';
import ScrollableAnchor from 'react-scrollable-anchor';
import MailIcon from '@material-ui/icons/Mail';
import "animate.css/animate.min.css";
import Banner from './Banner'
const steps = [
  {
    id: '0',
    message: 'Welcome to react chatbot!',
    trigger: '1',
  },
  {
    id: '1',
    message: 'Bye!',
    end: true,
  },
];


function App() {
  return(
    <div>
      <Container style={{backgroundColor:"black", height:200, top:0, left:0, position:"absolute"}}>
          <div>hello</div>
        </Container>
      <Parallax pages={2} >
        <ParallaxLayer offset={0} speed={1} >
          <Container style={{backgroundColor:"", paddingTop: 10, paddingBottom:20}}>
            <img src={logo} style={{height:50}}/>
            <Typography variant={"h6"} style={{color:"white"}}><b>Hey There </b> 👋</Typography>
            <Typography variant={"body"} style={{color:"white", opacity:0.8}}>Every year, there are billions of empty car seats going between cities, leading to congestion and pollution.</Typography>
          </Container>
          <ParallaxLayer offset={0} speed={0.5}>
            <Container  style={{backgroundColor:"",  paddingTop: 10, paddingBottom:10}}>
              <div style={{boxShadow: "2px 2px 3px #9999", backgroundColor:"white"}}>
                <Container  style={{backgroundColor:"",  paddingTop: 20, paddingBottom:30}}>
                <Typography variant={"subtitle1"} style={{color:"black", paddingBottom:10}}><b>Start a conversation</b></Typography>
                <Grid>
                  <GridItem sm={6} xs={6}>

                  </GridItem>
                </Grid>
                <AvatarGroup max={2} style={{paddingBottom:10}}>
                  <Avatar alt="Remy Sharp" src={propic1} />
                  <Avatar alt="Travis Howard" src={propic1} />
                </AvatarGroup>
                <Button
                  variant="outlined"
                  color="black"
                  style={{textTransform:"none", borderRadius:25}}
                  startIcon={<MailIcon />}
                >
                  Send us a message
                </Button>
                </Container>
              </div>
            </Container>
          </ParallaxLayer>
        </ParallaxLayer>
      </Parallax>
    </div>
  )
}

export default App;
