import React, {useEffect, useState}from 'react';
import './App.css';
import ChatBot from 'react-simple-chatbot';
import { ThemeProvider } from 'styled-components';
import ChatBanner from './ChatBanner';
import InfoIcon from '@material-ui/icons/Info';
import { FreeBreakfastOutlined, Info } from '@material-ui/icons';
import { Typography } from '@material-ui/core';
import styles from './Chat.css'; 
import {RemoveScrollBar} from 'react-remove-scroll-bar';
import { connect } from 'react-redux'
import { Redirect } from 'react-router-dom';

var stateData;
var chatID;
var m = "place";
var pointer = 0;
var triggerN = ""

//the Trigger
function Loader(props){

  if(stateData != null && chatID != null &&  stateData[chatID]){
    console.log("chatSection", stateData);
    if(pointer < stateData[chatID].length){
      m = stateData[chatID][pointer]
      pointer++;
      //triggerN = "message"
    }
    return(<div/>)
  }
  return(<div/>)
}

const steps = [
  {
    id: '1',
    component: <Loader/>,
    trigger: triggerN
  },
  {
    id: 'message',
    message: m,
    trigger: '1'
  }
]

const theme = {
    background: '#f5f8fb',
    fontFamily: 'Helvetica Neue',
    headerBgColor: '#000000',
    headerFontColor: '#fff',
    headerFontSize: '15px',
    botBubbleColor: '#EF6C00',
    botFontColor: '#fff',
    userBubbleColor: '#fff',
    userFontColor: '#4a4a4a',
  };

function ChatSection(props){

  stateData = props.data;
  chatID = props.id;
  if(props.data.conversationIDS > 0){
    return(
      <ThemeProvider theme={theme}>
          <ChatBot hideHeader={true} steps={steps} customStyle={{backgroundColor:'transparent', 'boxShadow': 'none'}} contentStyle={{height: props.height, backgroundColor:'transparent', }} style={{backgroundColor:'transparent'}} />
      </ThemeProvider>
  )
  } else {
    return(<div/>)
  }

}

const mapStateToProps = (state, ownProps) => {
  //let id = ownProps.match.params.post_id;

  return {
    data: state
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    //deletePost: (id) => dispatch(deletePost(id))
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(ChatSection);
