const initState = {
    sessionID: "",

  }

  const rootReducer = (state = initState, action) => {
    console.log(action);
    if(action.type === 'UPDATE_SESSION'){
     return {
       ...state,
       session: action
     }
    } else if(action.type === 'UPDATE_CONVERSATION_IDS'){
        return {
          ...state,
          conversationIDS: state.conversationIDS + action.message
        }
       }
    return state;
  }

  export default rootReducer
